const body=document.querySelector("body");body.setAttribute("style","display: flex; flex-direction: column;");const loginFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#email-login").value.trim(),c=document.querySelector("#password-login").value.trim();if(b&&c){const a=await fetch("/api/users/login",{method:"POST",body:JSON.stringify({email:b,password:c}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):alert("Incorrect email or password, please click on signup")}};document.querySelector(".login").addEventListener("click",loginFormHandler);