const body=document.querySelector("body");body.setAttribute("style","display: flex; flex-direction: column;");const signupFormHandler=async a=>{a.preventDefault();const b=document.querySelector("#username-signup").value.trim(),c=document.querySelector("#email-signup").value.trim(),d=document.querySelector("#password-signup").value.trim();if(b&&c&&d){if(!c.includes("@"))return void alert("Email address must contain an @ symbol");if(6>d.length)return void alert("Length of password must be 6 or more characters");const a=await fetch("/api/users",{method:"POST",body:JSON.stringify({username:b,email:c,password:d}),headers:{"Content-Type":"application/json"}});a.ok?document.location.replace("/dashboard"):alert("User already exists, please click on login")}else alert("User name, email and password must all be entered")};document.querySelector(".signup-form").addEventListener("submit",signupFormHandler);